import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import Header from './Header';
import Navbar from './Navbar';
import { ImHeartBroken } from 'react-icons/im';
import { RiPagesLine } from 'react-icons/ri';
import Button from 'react-bootstrap/Button';

export default function RecomForm() {
  const [b, setB] = useState([]);
  const params = useParams();
  console.log(params.gender)
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:8080/recommendation/googleImages?q=${params.gender}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            gender: params.gender,
            event: params.event,
            color: params.color,
            type: params.type,
          }),
        });
  
        if (response.ok) {
          const data = await response.json();
          console.log(data)
          setB(data);
        }
      } catch (error) {
        console.error(error);
      }
    };
  
    fetchData();
  }, [params.gender, params.event, params.color, params.type]);

  return (
    <div>
      <Header />
      <Navbar />
      <Container className="shadow">
        <Row>
          <p className="h1 text-success">Your Output</p>
        </Row>
        <hr />
        <Container>
          <Row>
            
             <p> {params.gender}</p>
            {params.event}
            {b.map((item, index) => (
              <Card style={{ width: '18rem' }} className="m-3 shadow" key={index}>
                <Card.Img variant="top" src={item} />
                <Card.Body>
                  <Card.Title>{item.gender}</Card.Title>
                  <Button variant="border border-3" className="m-2" style={{ backgroundColor: 'rgba(248,153,61,255)' }}>
                    <ImHeartBroken />
                  </Button>
                  <Button variant="border border-3 bg-info m-2">
                    <RiPagesLine />
                  </Button>
                </Card.Body>
              </Card>
            ))}
          </Row>
        </Container>
      </Container>
    </div>
  );
}
